parameters.param:
@echo off
cd %teamcity.build.workingDir%

SETLOCAL
CALL packages\KoreBuild\build\dnvm use default -runtime CLR -x86
@powershell -NoProfile -ExecutionPolicy unrestricted -Command "Get-ChildItem %mr.SourceFolder% project.json -rec -erroraction 'silentlycontinue' | Foreach { dnu build $_.FullName --configuration %mr.Configuration% }; exit $Lastexitcode"
@powershell -NoProfile -ExecutionPolicy unrestricted -Command "Get-ChildItem %mr.SourceFolder% *.nupkg -rec -erroraction 'silentlycontinue' | Where-Object {$_.FullName -match 'bin'} | Select-Object -Expand FullName | Foreach { Write-Host `#`#teamcity`[publishArtifacts `'$_`'`] }; exit $Lastexitcode"
